import React, { useState, useEffect } from "react";
import { FaHome, FaShoppingCart, FaHeart, FaBell, FaSearch } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import "./homePage.css";

const HomePage = ({ cart, setCart }) => {
  const [categories, setCategories] = useState([]);
  const [activeCategory, setActiveCategory] = useState("All");
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch categories dynamically
    const fetchedCategories = ["All", "Popular", "Pizza", "Burger", "Pasta", "Drinks", "Desserts"];
    setCategories(fetchedCategories);

    // Sample Product Data
    const sampleProducts = [
      { id: 1, name: "Cheese Pizza", description: "With Extra Cheese", price: 9.99, image: "pizza.jpg", category: "Pizza" },
      { id: 2, name: "Veggie Burger", description: "With Fresh Vegetables", price: 7.99, image: "burger.jpg", category: "Burger" },
      { id: 3, name: "Pasta Alfredo", description: "Creamy Alfredo Sauce", price: 8.99, image: "pasta.jpg", category: "Pasta" },
      { id: 4, name: "Lemonade", description: "Fresh and Cool", price: 3.99, image: "drinks.jpg", category: "Drinks" },
      { id: 5, name: "Chocolate Cake", description: "Rich and Moist", price: 5.99, image: "dessert.jpg", category: "Desserts" },
    ];
    setProducts(sampleProducts);
  }, []);

  const filteredProducts = activeCategory === "All" ? products : products.filter((product) => product.category === activeCategory);

  const handleAddToCart = (product, e) => {
    e.stopPropagation();
    setSelectedProduct(product);
    setIsPopupOpen(true);
  };

  const confirmAddToCart = () => {
    setCart((prevCart) => {
      const existingItem = prevCart.find((item) => item.id === selectedProduct.id);
      if (existingItem) {
        return prevCart.map((item) =>
          item.id === selectedProduct.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevCart, { ...selectedProduct, quantity: 1 }];
    });
    setIsPopupOpen(false);
    alert(`${selectedProduct.name} added to cart!`);
  };

  return (
    <div className="homepage">
      <header className="header">
        <h1>Find the best food for you</h1>
        <div className="profile-pic"></div>
      </header>

      <div className="search-bar">
        <input type="text" placeholder="Find Your Food..." />
        <FaSearch className="search-icon" />
      </div>

      <div className="category-container">
        <div className="category-scroll">
          {categories.map((category) => (
            <button
              key={category}
              className={`category-button ${activeCategory === category ? "active" : ""}`}
              onClick={() => setActiveCategory(category)}
            >
              {category}
            </button>
          ))}
        </div>
      </div>

      <div className="product-grid">
        {filteredProducts.map((product) => (
          <div
            key={product.id}
            className="product-card"
            onClick={() => navigate(`/detail`, { state: { product } })}
          >
            <img src={product.image} alt={product.name} className="product-image" />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <div className="product-footer">
              <span>${product.price.toFixed(2)}</span>
              <button className="add-to-cart" onClick={(e) => handleAddToCart(product, e)}>
                +
              </button>
            </div>
          </div>
        ))}
      </div>

      {isPopupOpen && selectedProduct && (
        <div className="popup-overlay">
          <div className="popup">
            <h2>{selectedProduct.name}</h2>
            <p>{selectedProduct.description}</p>
            <p>Price: ${selectedProduct.price.toFixed(2)}</p>
            <button onClick={confirmAddToCart}>Confirm</button>
            <button onClick={() => setIsPopupOpen(false)}>Cancel</button>
          </div>
        </div>
      )}

      <footer className="footer-nav">
        <FaHome className="nav-icon" />
        <FaShoppingCart onClick={() => navigate("/CartPage")} size={24} className="nav-icon" />
        <FaHeart className="nav-icon" />
        <FaBell className="nav-icon" />
      </footer>
    </div>
  );
};

export default HomePage;
