import React, { useState, useEffect } from 'react';
import { FaHome, FaShoppingCart, FaHeart, FaBell, FaSearch } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import './homePage.css';

const HomePage = () => {
  const [categories, setCategories] = useState(['All', 'Pizza', 'Burger', 'Pasta', 'Drinks']);
  const [activeCategory, setActiveCategory] = useState('All');
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  // Simulating fetching data from an API
  useEffect(() => {
    const fetchProducts = () => {
      const sampleProducts = [
        { id: 1, name: 'Cheese Pizza', description: 'With Extra Cheese', price: 9.99, image: 'pizza.jpg', category: 'Pizza' },
        { id: 2, name: 'Veggie Burger', description: 'With Fresh Vegetables', price: 7.99, image: 'burger.jpg', category: 'Burger' },
        { id: 3, name: 'Pasta Alfredo', description: 'Creamy Alfredo Sauce', price: 8.99, image: 'pasta.jpg', category: 'Pasta' },
        { id: 4, name: 'Lemonade', description: 'Fresh and Cool', price: 3.99, image: 'drinks.jpg', category: 'Drinks' },
      ];
      setProducts(sampleProducts);
    };
    fetchProducts();
  }, []);

  const filteredProducts = activeCategory === 'All' ? products : products.filter(product => product.category === activeCategory);

  const handleProductClick = (product) => {
    navigate('/detail', { state: { product } });
  };

  // ðŸ›’ Add to Cart Function
  const addToCart = (product, e) => {
    e.stopPropagation(); // âœ… Prevents navigation on button click
    setCart([...cart, product]);
    alert(`${product.name} added to cart!`); // âœ… Popup message
  };

  return (
    <div className="homepage">
      {/* Header Section */}
      <header className="header">
        <h1>Find the best food for you</h1>
        <div className="profile-pic"></div>
      </header>

      {/* Search Bar */}
      <div className="search-bar">
        <input type="text" placeholder="Find Your Food..." />
        <FaSearch className="search-icon" />
      </div>

      {/* Category Filter */}
      <div className="category-filter">
        {categories.map(category => (
          <button
            key={category}
            className={`category-button ${activeCategory === category ? 'active' : ''}`}
            onClick={() => setActiveCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Product Cards */}
      <div className="product-card" onClick={() => navigate(`/detail/${product.id}`)}>
  <img src={product.image} alt={product.name} className="product-image" />
  <h3>{product.name}</h3>
  <p>${product.price.toFixed(2)}</p>

  {/* Add to Cart button - Stop click from bubbling to parent */}
  <button 
    className="add-to-cart"
    onClick={(e) => {
      e.stopPropagation();  // Prevents clicking the button from triggering the parent divâ€™s onClick
      addToCart(product);
    }}
  >
    Add to Cart
  </button>
</div>





      {/* Footer Navigation */}
      <footer className="footer-nav">
        <FaHome className="nav-icon" />
        <FaShoppingCart onClick={() => navigate('/CartPage')} size={24} className="nav-icon" />
        <FaHeart className="nav-icon" />
        <FaBell className="nav-icon" />
      </footer>
    </div>
  );

  const HomePage = ({ cart, setCart }) => {
    const handleAddToCart = (product) => {
      setCart((prevCart) => {
        const existingItem = prevCart.find((item) => item.id === product.id);
        if (existingItem) {
          return prevCart.map((item) =>
            item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
          );
        }
        return [...prevCart, { ...product, quantity: 1 }];
      });
    };

    return (
      <div className="product-grid">
        {filteredProducts.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} className="product-image" />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <div className="product-footer">
              <span>${product.price.toFixed(2)}</span>
              <button
                className="add-btn" // Add button for HomePage
                onClick={() => handleAddToCart(product)}
              >
                Add
              </button>
            </div>
          </div>
        ))}
      </div>
    );
  };



};

export default HomePage;
